package attest

import (
	"bytes"
	"crypto/rsa"
	"encoding/base64"
	"math/big"
	"math/rand"
	"testing"
)

func decodeBase10(base10 string, t *testing.T) *big.Int {
	i, ok := new(big.Int).SetString(base10, 10)
	if !ok {
		t.Fatalf("failed decode of base10: %q", base10)
	}
	return i
}

func decodeBase64(in string, t *testing.T) []byte {
	out, err := base64.StdEncoding.DecodeString(in)
	if err != nil {
		t.Fatal(err)
	}
	return out
}

func ekCertSigner(t *testing.T) *rsa.PrivateKey {
	return &rsa.PrivateKey{
		PublicKey: rsa.PublicKey{
			N: decodeBase10("", t),
			E: 3,
		},
		D: decodeBase10("", t),
		Primes: []*big.Int{
			decodeBase10("", t),
			decodeBase10("", t),
		},
	}
}

func TestActivation(t *testing.T) {
	priv := ekCertSigner(t)
	rand := rand.New(rand.NewSource(123456))

	// These parameters represent an AK generated on a real-world, infineon TPM.
	params := ActivationParameters{
		AK: AttestationParameters{
			Public:            decodeBase64("", t),
			CreateData:        decodeBase64("AAAAAAAg47DEQpj8HBSa+/TImW+5JCeuQeRkm5NMpJWZG3hSuFUBAAsAIgALWI9hwDRB3zYSkannqM5z0J1coQNA1Jz/oCRxJQwTaNwAIgALmyFYBhHeIU3FUKIAPgXFD3NXyasP3siQviDEyH7avu4AAA==", t),
			CreateAttestation: decodeBase64("/1RDR4AaACIAC41+jhmEOue1MZhJjIk79ENar6i15rBvamXLpQnGTBCOAAAAAAAAD3GRNfU4syzJ1jQGATDCDteFC5C4ACIAC3ToMYGy9GXxcf8A0HvOuLOHbU7HPEppM47C7CMcU8TtACBDmJFUFO1f5+BYevaYdd3VtfMCsxIuHhoTZJczzLP2BA==", t),
			CreateSignature:   decodeBase64("", t),
		},
		EK: &rsa.PublicKey{
			E: priv.E,
			N: priv.N,
		},
		Rand: rand,
	}

	secret, _, err := params.Generate()
	if err != nil {
		t.Fatalf("Generate() returned err: %v", err)
	}
	if got, want := secret, decodeBase64("0vhS7HtORX9uf/iyQ8Sf9WkpJuoJ1olCfTjSZuyNNxY=", t); !bytes.Equal(got, want) {
		t.Fatalf("secret = %v, want %v", got, want)
	}
}

func TestECCActivation(t *testing.T) {
	priv := ekCertSigner(t)
	rand := rand.New(rand.NewSource(123456))

	// These parameters represent an ECC P256 AK generated on a real-world,
	// Google vTPM.
	params := ActivationParameters{
		AK: AttestationParameters{
			Public:            decodeBase64("ACMACwAFBHIAIJ3/y/NsODrmmfuYaNxty4nXFTiEvigDkiwSQVi/rSKuABAAGAAEAAMAEAAgydcfFWW6O7PjW9vnOE4IDx3545TxylD1iVHP8MIFI78AIJuD/QM9EbqM+3SEl7PgiXlWV1NhmvnmE2AHiEfI/hUn", t),
			CreateData:        decodeBase64("AAAAAAAg47DEQpj8HBSa+/TImW+5JCeuQeRkm5NMpJWZG3hSuFUBAAsAIgALLEyPGewwEIKqPNw9Lx7QXsfp0MsOZFt4EzHFT4tSXekAIgALf7O+OxqTNzTuIhi1YGQoulPZoyxsNKHpBgT4dHqT3+UAAA==", t),
			CreateAttestation: decodeBase64("/1RDR4AaACIAC8Uqr5OAkfcyLJ2gLU2oSvIoPi8XdoLHGdpS5h/JJz8aAAAAAAAEXezQqarG49k44bBnAUna81DZr1xhACIAC3AoqIzDrusUtdH3uAwqbqUrybtu35H1XPQDyLBHVGF+ACCKEKpwL1LFbw/IG+vtJ6CtXHIYBhVWyrkAqYLkHleYMw==", t),
			CreateSignature:   decodeBase64("ABgABAAgbTEBcZvjb9uEEYZCiSqPh/XO0BZQS+egnJ8WKtpSbmkAIDNgvF9iyiOCvd5480hOCzjRj7GP3YZ3XqjEVvN3Q3Ca", t),
		},
		EK: &rsa.PublicKey{
			E: priv.E,
			N: priv.N,
		},
		Rand: rand,
	}

	secret, _, err := params.Generate()
	if err != nil {
		t.Fatalf("Generate() returned err: %v", err)
	}
	if got, want := secret, decodeBase64("0vhS7HtORX9uf/iyQ8Sf9WkpJuoJ1olCfTjSZuyNNxY=", t); !bytes.Equal(got, want) {
		t.Fatalf("secret = %v, want %v", got, want)
	}
}
